import React, { useState,useEffect } from 'react';    
import ReactCardFlip from 'react-card-flip'; 

export const Box=(props)=> {
    //const color = colors[Math.floor(Math.random() * 9)];
    const [isFlipped, setisFlipped] = useState(false);
    const [fotok,setFotok]=useState([])
    const[dbFoto,setDbFoto]=useState(0)
  
   const handleClick=()=>{
      setisFlipped(!isFlipped)
    }
  
   console.log("fotok darabja:"+props.adatok["fotok"]+" dbFoto="+dbFoto)

    while(dbFoto>0){
     let urlImg="https://raw.githubusercontent.com/kmagdi/KSZC-Data/master/"+props.id+"img"+(dbFoto-1)+".jpg"
     console.log(urlImg)
     setDbFoto(dbFoto-1)
     setFotok(fotok.push(urlImg))
   }
    
    return (
      <div className="col">
        {}
         <ReactCardFlip isFlipped={isFlipped} flipDirection="vertical">
          <div className="card col-md-3">
            {/*This is the front of the card.*/}
            {props.tema}
            <button onClick={handleClick}>n√©zd meg</button>
          </div>
  
          <div className="card col-md-3">
            {/*This is the back of the card.*/}
            {props.adatok[props.id]}
            <button onClick={handleClick}>-</button>
          </div>
        </ReactCardFlip>
      </div>
     
    );
  }
