import React, { useState } from 'react'
import ReactHowler from 'react-howler'

export const Music=()=>{
  const { play, setPlay } = useState(false)
  return (
    <div>
      <ReactHowler src='./assets/T1.mp3' playing={true} />
      <button onClick={() => setPlay(!play)}>
        { play ? 'Pause' : 'Play' }
      </button>
    </div>
  )
}
