import React, { useState,useEffect } from 'react';     

export const Box=({ word })=> {
    //const color = colors[Math.floor(Math.random() * 9)];
    const [visible, setVisible] = useState(true);
  
    function hideMe(){
      setVisible(false);
    }
    function showMe(){
        setVisible(true)
    }
  
    let style ={  borderColor: "black", backgroundColor: "green" };
    //if (!visible) style.display = "none";
  
    return (
      <div className="box" style={style}>
         <div className="center">{word}</div>
        <button className="button top-corner" onClick={hideMe}><i className="fas fa-angle-right" /></button>
        <button className="button bottom-corner" onClick={showMe}><i className="fas fa-angle-left" /></button>
      </div>
    );
  }
