import React from 'react';
import {Box} from "./Box"
import { FullscreenBanner } from './FullscreenBanner';
import './Details.css'
import useSound from 'use-sound';



export const Detail=(props)=>{
    console.log(props.adatok.kod+".mp3")

    const zene = () => {
        const [play] = useSound("./assets/"+props.adatok.kod+".mp3");
       return  <button onClick={play}>zene</button>;
    }

    return(
        <React.Fragment>
             
            <FullscreenBanner title={props.adatok.leiras} />
            <div className="col">
                {Object.keys(props.temaKorok).filter(obj=>props.temaKorok[obj].length>5).map((obj, i) => <Box key={obj} tema={props.temaKorok[obj]} id={obj} adatok={props.adatok}/>)}
            </div>
            <div className="col">
                {props.fotok.map((e,i) => <img key={e} src={e} alt="foto"/>)}
            </div>
         <zene />
        </React.Fragment>
    )
}
